public class Resuse {

   
    public void add(int a,int b)
    {
        double x=a+b;
        System.out.println("x is : "+x);
    }
    public void add(double a,double b)
    {
        double x=a+b;
        System.out.println("x is : "+x);
    }
    public void add(double a,double b,double c)
    {
        double x=a+b+c;
        System.out.println("x is : "+x);
    }
    
}
