public class array {

    static int number[] = {10, 20, 30, 40};
    static int sum = 0;

    public static void main(String[] args) {
        for (int i = 0; i < number.length; i++) {
            sum += number[i];
        }
        System.out.println("The sum is: " + sum);
    }
}
